package com.example.controller;import com.example.dto.ApiResponse;import com.example.enums.Language;import com.example.enums.Like;import com.example.service.VideoLikeService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.security.access.prepost.PreAuthorize;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/api/v1/video-like")public class VideoLikeController {    @Autowired    private VideoLikeService videoLikeService;    @PostMapping("/close/create/like/{videoId}")    @PreAuthorize("hasAnyRole('USER', 'ADMIN')")    public ResponseEntity<ApiResponse> like(@PathVariable String videoId,                                            @RequestParam(defaultValue = "en") Language lang) {        ApiResponse response = videoLikeService.like(videoId, lang);        if (response.getStatus()) {            return ResponseEntity                    .status(HttpStatus.CREATED)                    .body(response);        } else {            return ResponseEntity.badRequest().body(response);        }    }    @PostMapping("/close/create/dislike/{videoId}")    @PreAuthorize("hasAnyRole('USER', 'ADMIN')")    public ResponseEntity<ApiResponse> dislike(@PathVariable String videoId,                                            @RequestParam(defaultValue = "en") Language lang) {        ApiResponse response = videoLikeService.dislike(videoId, lang);        if (response.getStatus()) {            return ResponseEntity                    .status(HttpStatus.CREATED)                    .body(response);        } else {            return ResponseEntity.badRequest().body(response);        }    }}